import React, { Component } from 'react'
import {Button} from 'react-bootstrap'
import NewFlight from './NewFlight.js'

export default class FlightCard extends Component {
    state = {
        flight_info: this.props.flights.map(flight => flight.flight_info),
        date: this.props.flights.map(flight => flight.date),
        time: this.props.flights.map(flight => flight.time)
    }

    newFlightHandler = (data) => {
        this.setState({flight_info: [...this.state.flight_info, data.flight_info]})
        this.setState({date: [...this.state.date, data.date]})
        this.setState({time: [...this.state.time, data.time]})
    }

    handleFilter = filter => {
        this.setState({filter})
      }

    handleChange=(e)=> {
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
    } 

    render() {
        return (
            <div>
            <Button> 
                <strong>Collaborator:</strong>
                    <select >
                        {this.props.collaborators.map(collaborator => 
                        <option id="collaborator" name="collaborator" value={collaborator.user.id} onChange={this.handleChange}>{collaborator.user.name}</option>
                    )}
                    </select><br/>
                <NewFlight 
                event_id={this.props.event_id} 
                newFlight={this.newFlightHandler} 
                handleFilter={this.handleFilter} 
                collaborators={this.props.collaborators} 
                collaborator_id={this.state.collaborator_id}
                /> </Button>
            </div>
        )
    }
}
