import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccommodationsCard from './AccommodationsCard'

let activitiesURL = 'http://localhost:3000/activities'
let accommodationsURL = 'http://localhost:3000/accommodations'
let flightsURL = "http://localhost:3000/flights"
let expensesURL = "http://localhost:3000/expenses"

export default class EventCard extends Component {
    constructor(){
        super()
        this.state= {
            activities: [],
            accommodations: [],
            flights: [],
            expenses: []
        }
    }

    // componentDidMount(){
    //     this.fetchActivities()
