    //     })
    //     .then(res => res.json())
    //     .then(activities => this.setState({activities}))
    // }

    // fetchAccommodations = () => {
    //     fetch(accommodationsURL, {
    //         method:'GET',
    //         headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
    //     })
    //     .then(res => res.json())
    //     .then(accommodations => this.setState({accommodations}))
    // }

    // fetchFlights = () => {
    //     fetch(flightsURL, {
    //         method:'GET',
    //         headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
    //     })
    //     .then(res => res.json())
