    // }

    // fetchExpenses = () => {
    //     fetch(expensesURL, {
    //         method:'GET',
    //         headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
    //     })
    //     .then(res => res.json())
    //     .then(expenses => this.setState({expenses}))
    // }

    render() {


        return (
            <div>
            <h5>{this.props.event.name} <br />  </h5>
            {this.props.event.date} <br /> 
            {this.props.event.location} <br/><br/>

            <h6> Accommodations</h6>
            {this.props.event.accommodations.map(accommodation => accommodation.location)} <br/><br/>


            <h6> Activities </h6>
            {this.props.event.activities.map(activity => activity.description)}<br/><br/>


            <h6> Flights </h6>
            {this.props.event.flights.map(flight => flight.flight_info)}<br/><br/>

            <h6> Expenses</h6> 
            {this.props.event.expenses.map(expense => expense.description)}<br/>
            ${this.props.event.expenses.map(expense => expense.total)}<br/><br/>


            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
