import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccomCard from './AccomCard.js'
import ActCard from './ActCard.js'
import FlightCard from './FlightCard.js'
import NewFlight from './NewFlight.js'


export default class EventCard extends Component {
    constructor(){
        super()
    }

    render() {
        return (
            <div>
            <h5>{this.props.name} <br />  </h5>
            {this.props.date} <br /> 
            {this.props.location} <br/><br/>

            <h6> Collaborators</h6>

            <h6> Accommodations</h6>
            <AccomCard event={this.props.event} accommodations={this.props.accommodations} /> <br/> <br/>

            <h6> Activities </h6>
            <ActCard event={this.props.event} activities={this.props.activities} /> <br/> <br/>

            <h6> Flights </h6>

            {/* {this.props.collaborators.map(collaborator=> 
            <FlightCard
                name={collaborator.user.name}
            />    
            )} */}

            {this.props.event.flights.map(flight => 
                <FlightCard 
                collab={flight.collaborator.user.name}
                event={this.props.event} 
                flight = {flight}
                flight_info={flight.flight_info} 
                date={flight.date} 
                time={flight.time} 
                collaborator={flight.collaborator_id} /> 
            )} <br/>

            <Button> <NewFlight event_id={this.props.event.id} newFlight={this.newFlightHandler} handleFilter={this.handleFilter} collaborators={this.props.event.collaborators}/> </Button>

            <h6> Expenses</h6> 
            {this.props.event.expenses.map(expense => expense.description)}<br/>
            ${this.props.event.expenses.map(expense => expense.total)}<br/><br/>


            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
