import React, { Component } from 'react'
import EventCard from './EventCard'
import {Button, Card, Row, Col} from 'react-bootstrap'

let eventsURL = 'http://localhost:3000/events/'

export default class EventContainer extends Component {
    state={
        events: []
    }

    componentDidMount(){
        fetch(eventsURL, {
            method:'GET',
            headers:{
                'Content-Type': 'application/json',
                'Auth-Key': localStorage.getItem('auth_key')
            }
        })
        .then(res => res.json())
        .then(events => {
            this.setState({events})
        })
    }

    componentDidUpdate(prevProps, prevState){
        if (prevState.event !== this.state.cart_items){
           fetch(item_orderUrl)
                   .then(res=>res.json())
                   .then(cart_items=> this.setState({cart_items}))
        }

    }

    handleDelete = (event) => {
        fetch(`http://localhost:3000/events/${event.id}`, {
            method: 'DELETE',
            headers:{
                'Content-Type': 'application/json',
                'Auth-Key': localStorage.getItem('auth_key')
            }
        })
        let array = this.state.events;

        let i = array.indexOf(event);
            if (i > -1) {
                array.splice(i, 1);
            }
        this.setState({
            events: array
        })
    }


    render() {
        const myEvents = this.state.events.map(event => 
            <Card > 
                <EventCard 
                    key={event.id}
                    event={event}
                    name={event.name} 
                    date={event.date}
                    collaborators={event.collaborators} 
                    flicollab={event.flights.map(flight=>flight.collaborator.user.name)}
                    activities={event.activities}
                    accommodations={event.accommodations}
                    expenses={event.expenses}
                    flights={event.flights}
                    location={event.location}
                    handleDelete={this.handleDelete}
                    handleEditEvent={this.handleEditEvent}
                    />
            </Card> 
            )

        return (
            <div>
                {myEvents}
            </div>
        )
    }
}
