import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccommodationsCard from './AccommodationsCard'

let activitiesURL = 'http://localhost:3000/activities'
let accommodationsURL = 'http://localhost:3000/accommodations'


export default class EventCard extends Component {
    constructor(){
        super()
        this.state= {
            activities: [],
            accommodations: []
        }
    }

    componentDidMount(){
        this.fetchActivities()
        this.fetchAccommodations()
    }

    fetchActivities = () => {
        fetch(activitiesURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(activities => this.setState({activities}))
    }

    fetchAccommodations = () => {
        fetch(accommodationsURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(accommodations => this.setState({accommodations}))
    }

