            </AccommodationsCard>)

        return (
            <div>
            <h5>{this.props.event.name} <br />  </h5>
            {this.props.event.date} <br /> 
            {this.props.event.location} <br/><br/>

            <h6> Accommodations</h6>

            {this.state.accommodations.map (accommodation => accommodation.location)} <br/><br/>

            <h6> Activities </h6>
            {this.state.activities.map (activity => activity.description)} <br/><br/>

            <h6> Flights </h6> <br />

            <h6> Expenses</h6> <br />

            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
