import React, { Component } from 'react'
import { Dropdown } from 'semantic-ui-react'
import NewExpense from './NewExpense.js'

export default class ExpenseCard extends Component {
    state = {
        filter: " ",
    }

    handleFilter = filter => {
        console.log(filter)
        this.setState({filter})
      }

    render() {
        return (
            <div>
                <strong>Collaborator:</strong>
                    <select onChange={(e) => this.handleFilter(e.target.value)} >
                        {this.props.collaborators.map(collaborator => 
                        <option id="collaborator" name="collaborator" value={collaborator.id} >{collaborator.user.name}</option>
                    )}
                    </select><br/>
                    {this.props.collaborators.map(collaborator => ({key: collaborator.id, value:collaborator.id, text: collaborator.id}))}

                    <Dropdown
                        placeholder='Select Friend'
                        fluid
                        selection
                        options={["a", "b", "c"]}
                    />



                <NewExpense 
                event_id={this.props.event_id} 
                newExpense={this.props.newExpense} 
                filter={this.state.filter} 
                collaborators={this.props.collaborators} 
                handleClose={this.props.handleClose}
                />
            </div>
        )
    }
}
