import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccomCard from './AccomCard.js'
import NewAccom from './NewAccom.js'

export default class EventCard extends Component {
    constructor(){
        super()
    }

    render() {
        const myAccom = this.props.event.acommodations.map(accommodation => 
            <AccomCard 
                accommodation = {accommodation}
                location = {accommodation.location}
            />
            )

        return (
            <div>
            <h5>{this.props.event.name} <br />  </h5>
            {this.props.event.date} <br /> 
            {this.props.event.location} <br/><br/>

            <h6> Accommodations</h6>
            {myAccom}

            {this.props.event.acommodations.map(accommodation => 
            <AccomCard 
                accommodation = {accommodation}
                location = {accommodation.location}
            />
            )}

            {/* <AccomCard accommodation={this.props.event.accommodations}/> */}

            {/* {this.props.event.accommodations.map(accommodation => accommodation.location)}<br/><br/>
            <Button href="/accommodation"> + </Button> */}

            <h6> Activities </h6>
            {this.props.event.activities.map(activity => activity.description)}<br/><br/>
            <Button> + </Button>


            <h6> Flights </h6>
            {this.props.event.flights.map(flight => flight.flight_info)}<br/><br/>

            <h6> Expenses</h6> 
            {this.props.event.expenses.map(expense => expense.description)}<br/>
            ${this.props.event.expenses.map(expense => expense.total)}<br/><br/>


            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
