import React from 'react'
import { Component } from 'react';
import {withRouter} from 'react-router';
// import {Button} from 'react-bootstrap'
import { Button, Checkbox, Form } from 'semantic-ui-react'


class Login extends Component {
  constructor(){
      super()
      this.state={
          email: "",
          password: "",
      }
  }

  handleChange=(e)=> {
      const {name, value} = e.target
      this.setState({
          [name]: value
      })
  }

  handleSubmit=(e)=>{
      e.preventDefault()
      const logUser= {
              email: this.state.email,            
              password: this.state.password    
      }

      fetch('http://localhost:3000/login',{
          method: 'POST',
          headers:{
                "Accept": "application/json",
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(logUser)
        })
        .then(res=>res.json())
        .then(token=> {
            if (token["auth_key"]){
                localStorage.setItem('auth_key', token["auth_key"])
                this.props.handleLogin(token)
                this.props.history.push('/dashboard')
            }else{
                alert(token["msg"])
            }
        })
    }

  render(){
    return (
        <Form onSubmit={this.handleSubmit}>
            <Form.Field value={this.state.email} onChange={this.handleChange}>
                <input placeholder='Email' />
            </Form.Field>
            <Form.Field>
                <input placeholder='Last Name' />
            </Form.Field>
            <Button type='submit'>Submit</Button>
        </Form>
        // <form onSubmit={this.handleSubmit}>
        //     <input type="text" name="email" value={this.state.email} placeholder ="Email" onChange={this.handleChange}/><br></br>        
        //     <input type="password" name="password" value={this.state.password} placeholder= "Password" onChange={this.handleChange}/><br></br>
        //     <Button type="submit" text-align="center">Submit</Button>
        //     <h4>{this.state.errorMsg}</h4>
        // </form>
    )
  }
}

export default withRouter(Login);
