import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'

let activitiesURL = 'http://localhost:3000/activitiesURL'

export default class EventCard extends Component {
    constructor(){
        super()
        this.state= {
            activities: " "
        }
    }

    render() {
        return (
            <div>
            {this.props.event.name} <br /> 
            {this.props.event.date} <br /> 
            {this.props.event.location} <br /> <br />
            Accommodations <br /> <br />
            Activities <br /> <br />
            Flights <br /> <br />
            Expenses <br /> <br />
            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
