import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccomCard from './AccomCard.js'
import ActCard from './ActCard.js'
import FlightCard from './FlightCard.js'
import NewFlight from './NewFlight.js'
import ActCRUD from './ActCRUD.js'
import FlightCRUD from './FlightCRUD.js'

export default class EventCard extends Component {

    render() {
        console.log(this.props.event)
        return (
            <div>
            <h5>{this.props.name} <br/> </h5>
            {this.props.date} <br/> 
            {this.props.location} <br/><br/>

{/* ACCOMODATIONS  */}

            <h6> Accommodations</h6>
            <AccomCard 
            event={this.props.event} 
            accommodations={this.props.accommodations} 
            newAccom={this.props.newAccom}
            /> 
            <br/> <br/>

{/* ACTIVITIES */}

            <h6> Activities </h6>
            {this.props.activities.map(activity=> 
                <ActCard 
                event={this.props.event} 
                activity={activity}
                description={activity.description}
                date={activity.date}
                time={activity.time}
                newAct={this.props.newActHandler}
                /> 
                )} <br/>

            <Button> 
                <ActCRUD 
                event_id={this.props.event.id} 
                activities={this.props.activities}
                /> 
            </Button> <br /> 

{/* FLIGHTS */}

            {/* <h6> Flights </h6>
            {this.props.flights.map(flight => 
                <FlightCard 
                collabname={flight.collaborator.user.name}
                event={this.props.event} 
                flight = {flight}
                flight_info={flight.flight_info} 
                date={flight.date} 
                time={flight.time} 
             /> 
            )} <br/>

            <Button> 
                <FlightCRUD 
                event_id={this.props.event.id}
                flights={this.props.flights}
                collaborators={this.props.collaborators}
                /> 
                </Button> */}

{/* FLIGHTS */}

            {/* <h6> Expenses</h6> 
            {this.props.expenses.map(expense => expense.description)}<br/>
            ${this.props.expenses.map(expense => expense.total)}<br/><br/> */}

            <Row> 
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
