import {
    Timeline,
    Events,
    UrlButton,
    ImageEvent,
    TextEvent,
    YouTubeEvent,
  } from '@merc/react-timeline';

  import React, { Component } from 'react'

  export default class Itinerary extends Component {
      render() {
        const theme = createTheme(themes.default, {
            timeline: {
              backgroundColor: '#070D24',
            },
            timelineTrack: {
              background: `linear-gradient(
                to bottom, #5ec0a7 0%,#070d24 100%
              )`,
              width: '2px',
            },
            date: {
              backgroundColor: '#3DA588',
              color: '#fff',
            },
            marker: {
              borderRadius: '4px',
              borderColor: '#070D24',
              borderWidth: '3px',
              transform: 'rotate(45deg)',
              backgroundColor: '#BF2D51',
            },
            button: {
              backgroundColor: '#000958',
            },
          });
          return (
              <div>
                    <Timeline >
                    <Events >
                        <TextEvent date={this.props.date} text={this.props.time}> 
                            <div> 
                                {this.props.description}
                            </div>
                        </TextEvent>
                    </Events>
                    </Timeline>
              </div>
          )
      }
  }
