import React, { Component } from 'react'
import {Button, Row, Col} from 'react-bootstrap'
import AccommodationsCard from './AccommodationsCard'

let activitiesURL = 'http://localhost:3000/activities'
let accommodationsURL = 'http://localhost:3000/accommodations'
let flightsURL = "http://localhost:3000/flights"
let expensesURL = "http://localhost:3000/expenses"

export default class EventCard extends Component {
    constructor(){
        super()
        this.state= {
            activities: [],
            accommodations: [],
            flights: [],
            expenses: []
        }
    }

    componentDidMount(){
        this.fetchActivities()
        this.fetchAccommodations()
        this.fetchFlights()
        this.fetchExpenses()
    }

    fetchActivities = () => {
        fetch(activitiesURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(activities => this.setState({activities}))
    }

    fetchAccommodations = () => {
        fetch(accommodationsURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(accommodations => this.setState({accommodations}))
    }

    fetchFlights = () => {
        fetch(flightsURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(flights => this.setState({flights}))
    }

    fetchExpenses = () => {
        fetch(expensesURL, {
            method:'GET',
            headers: {'Authorization': `Bearer ${localStorage.getItem("token")}`}
        })
        .then(res => res.json())
        .then(expenses => this.setState({expenses}))
    }

    render() {


        return (
            <div>
            <h5>{this.props.event.name} <br />  </h5>
            {this.props.event.date} <br /> 
            {this.props.event.location} <br/><br/>

            <h6> Accommodations</h6>
            {this.props.event.accommodations.map(accommodation => accommodation.location)} <br/><br/>


            <h6> Activities </h6>
            {this.props.event.activities.map(activity => activity.description)}<br/><br/>


            <h6> Flights </h6>
            {this.props.event.flights.map(flight => flight.flight_info)}<br/><br/>

            <h6> Expenses</h6> 
            {this.props.event.activities.map(activity => activity.description)}<br/>
            ${this.props.event.activities.map(activity => activity.description)}<br/><br/>


            <Row> 
                <Col> <Button> Edit Event </Button></Col>
                <Col> <Button onClick={() => this.props.handleDelete(this.props.event)} > Delete </Button> </Col>
            </Row>
            </div>
        )
    }
}
