import * as React from 'react'

import { SemanticShorthandItem } from 'semantic-ui-react/dist/commonjs/generic'
import { LabelProps } from 'semantic-ui-react/dist/commonjs/elements/Label'
import { StrictInputProps } from 'semantic-ui-react/dist/commonjs/elements/Input'
import { StrictFormFieldProps } from 'semantic-ui-react/dist/commonjs/collections/Form/FormField'

export interface FormInputProps extends StrictFormInputProps {
  [key: string]: any
}

export interface StrictFormInputProps extends StrictFormFieldProps, StrictInputProps {
  /** An element type to render as (string or function). */
  as?: any

  /** A FormField control prop. */
  control?: any

  /** Individual fields may display an error state along with a message. */
  error?: any

  /** Shorthand for a Label. */
  label?: SemanticShorthandItem<LabelProps>
}

declare const FormInput: React.StatelessComponent<FormInputProps>

export default FormInput
