import * as React from 'react'

import { SemanticShorthandContent } from 'semantic-ui-react/dist/commonjs/generic'
import CommentAction from 'semantic-ui-react/dist/commonjs/views/Comment/CommentAction'
import CommentActions from 'semantic-ui-react/dist/commonjs/views/Comment/CommentActions'
import CommentAuthor from 'semantic-ui-react/dist/commonjs/views/Comment/CommentAuthor'
import CommentAvatar from 'semantic-ui-react/dist/commonjs/views/Comment/CommentAvatar'
import CommentContent from 'semantic-ui-react/dist/commonjs/views/Comment/CommentContent'
import CommentGroup from 'semantic-ui-react/dist/commonjs/views/Comment/CommentGroup'
import CommentMetadata from 'semantic-ui-react/dist/commonjs/views/Comment/CommentMetadata'
import CommentText from 'semantic-ui-react/dist/commonjs/views/Comment/CommentText'

export interface CommentProps extends StrictCommentProps {
  [key: string]: any
}

export interface StrictCommentProps {
  /** An element type to render as (string or function). */
  as?: any

  /** Primary content. */
  children?: React.ReactNode

  /** Additional classes. */
  className?: string

  /** Comment can be collapsed, or hidden from view. */
  collapsed?: boolean

  /** Shorthand for primary content. */
  content?: SemanticShorthandContent
}

interface CommentComponent extends React.StatelessComponent<CommentProps> {
  Action: typeof CommentAction
  Actions: typeof CommentActions
  Author: typeof CommentAuthor
  Avatar: typeof CommentAvatar
  Content: typeof CommentContent
  Group: typeof CommentGroup
  Metadata: typeof CommentMetadata
  Text: typeof CommentText
}

declare const Comment: CommentComponent

export default Comment
