import * as React from 'react'

import { SemanticShorthandCollection } from 'semantic-ui-react/dist/commonjs/generic'
import FeedContent from 'semantic-ui-react/dist/commonjs/views/Feed/FeedContent'
import FeedDate from 'semantic-ui-react/dist/commonjs/views/Feed/FeedDate'
import FeedEvent, { FeedEventProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedEvent'
import FeedExtra from 'semantic-ui-react/dist/commonjs/views/Feed/FeedExtra'
import FeedLabel from 'semantic-ui-react/dist/commonjs/views/Feed/FeedLabel'
import FeedMeta from 'semantic-ui-react/dist/commonjs/views/Feed/FeedMeta'
import FeedLike from 'semantic-ui-react/dist/commonjs/views/Feed/FeedLike'
import FeedSummary from 'semantic-ui-react/dist/commonjs/views/Feed/FeedSummary'
import FeedUser from 'semantic-ui-react/dist/commonjs/views/Feed/FeedUser'

export interface FeedProps extends StrictFeedProps {
  [key: string]: any
}

export interface StrictFeedProps {
  /** An element type to render as (string or function). */
  as?: any

  /** Primary content. */
  children?: React.ReactNode

  /** Additional classes. */
  className?: string

  /** Shorthand array of props for FeedEvent. */
  events?: SemanticShorthandCollection<FeedEventProps>

  /** A feed can have different sizes. */
  size?: 'small' | 'large'
}

interface FeedComponent extends React.StatelessComponent<FeedProps> {
  Content: typeof FeedContent
  Date: typeof FeedDate
  Event: typeof FeedEvent
  Extra: typeof FeedExtra
  Label: typeof FeedLabel
  Meta: typeof FeedMeta
  Like: typeof FeedLike
  Summary: typeof FeedSummary
  User: typeof FeedUser
}

declare const Feed: FeedComponent

export default Feed
