import * as React from 'react'

import { SemanticShorthandItem } from 'semantic-ui-react/dist/commonjs/generic'
import { FeedContentProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedContent'
import { FeedDateProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedDate'
import { FeedLabelProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedLabel'
import { FeedMetaProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedMeta'
import { FeedSummaryProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedSummary'
import { FeedExtraProps } from 'semantic-ui-react/dist/commonjs/views/Feed/FeedExtra'

export interface FeedEventProps extends StrictFeedEventProps {
  [key: string]: any
}

export interface StrictFeedEventProps {
  /** An element type to render as (string or function). */
  as?: any

  /** Primary content. */
  children?: React.ReactNode

  /** Additional classes. */
  className?: string

  /** Shorthand for FeedContent. */
  content?: SemanticShorthandItem<FeedContentProps>

  /** Shorthand for FeedDate. */
  date?: SemanticShorthandItem<FeedDateProps>

  /** Shorthand for FeedExtra with images. */
  extraImages?: SemanticShorthandItem<FeedExtraProps>

  /** Shorthand for FeedExtra with content. */
  extraText?: SemanticShorthandItem<FeedExtraProps>

  /** An event can contain icon label. */
  icon?: SemanticShorthandItem<FeedLabelProps>

  /** An event can contain image label. */
  image?: SemanticShorthandItem<FeedLabelProps>

  /** Shorthand for FeedMeta. */
  meta?: SemanticShorthandItem<FeedMetaProps>

  /** Shorthand for FeedSummary. */
  summary?: SemanticShorthandItem<FeedSummaryProps>
}

declare const FeedEvent: React.StatelessComponent<FeedEventProps>

export default FeedEvent
