import * as React from 'react'

import {
  SemanticShorthandContent,
  SemanticShorthandItem,
  SemanticVERTICALALIGNMENTS,
} from 'semantic-ui-react/dist/commonjs/generic'
import { ItemDescriptionProps } from 'semantic-ui-react/dist/commonjs/views/Item/ItemDescription'
import { ItemExtraProps } from 'semantic-ui-react/dist/commonjs/views/Item/ItemExtra'
import { ItemHeaderProps } from 'semantic-ui-react/dist/commonjs/views/Item/ItemHeader'
import { ItemMetaProps } from 'semantic-ui-react/dist/commonjs/views/Item/ItemMeta'

export interface ItemContentProps extends StrictItemContentProps {
  [key: string]: any
}

export interface StrictItemContentProps {
  /** An element type to render as (string or function). */
  as?: any

  /** Primary content. */
  children?: React.ReactNode

  /** Additional classes. */
  className?: string

  /** Shorthand for primary content. */
  content?: SemanticShorthandContent

  /** Shorthand for ItemDescription component. */
  description?: SemanticShorthandItem<ItemDescriptionProps>

  /** Shorthand for ItemExtra component. */
  extra?: SemanticShorthandItem<ItemExtraProps>

  /** Shorthand for ItemHeader component. */
  header?: SemanticShorthandItem<ItemHeaderProps>

  /** Shorthand for ItemMeta component. */
  meta?: SemanticShorthandItem<ItemMetaProps>

  /** Content can specify its vertical alignment. */
  verticalAlign?: SemanticVERTICALALIGNMENTS
}

declare const ItemContent: React.ComponentClass<ItemContentProps>

export default ItemContent
